import { join, resolve, relative, dirname } from 'path'
import webpack from 'webpack'

// This plugin modifies the require-ensure code generated by Webpack
// to work with @symph/joy SSR
export default class JoySsrImportPlugin {
  apply (compiler:  any) {
    compiler.hooks.compilation.tap('JoySSRImport', (compilation: any) => {
      compilation.mainTemplate.hooks.requireEnsure.tap('JoySSRImport', (code: any, chunk: any) => {
        // Update to load chunks from our custom chunks directory
        const outputPath = resolve('/')
        const pagePath = join('/', dirname(chunk.name))
        const relativePathToBaseDir = relative(pagePath, outputPath)
        // Make sure even in windows, the path looks like in unix
        // Node.js require system will convert it accordingly
        let relativePathToBaseDirNormalized = relativePathToBaseDir.replace(/\\/g, '/')
        if (relativePathToBaseDirNormalized === '') {
          relativePathToBaseDirNormalized = '.'
        }
        return code.replace('require("./"', `require("${relativePathToBaseDirNormalized}/"`).replace('readFile(join(__dirname', `readFile(join(__dirname, "${relativePathToBaseDirNormalized}"`)
      })
    })
  }
}
